interface ApiModel {
  id: string;
  name: string;
  description: string;
  task: {
    id: string;
    name: string;
    description: string;
  };
  created_at: string;
  tags: string[];
  properties: Array<{
    property_id: string;
    value: string | Array<{ unit: string; price: number; currency: string }>;
  }>;
}

interface JsonSchema {
  type?: string;
  properties?: Record<string, JsonSchema>;
  items?: JsonSchema;
  oneOf?: JsonSchema[];
  anyOf?: JsonSchema[];
  allOf?: JsonSchema[];
  required?: string[];
  enum?: string[];
  description?: string;
  minLength?: number;
  maxItems?: number;
  default?: any;
  contentType?: string;
  title?: string;
  format?: string;
  minimum?: number;
  maximum?: number;
}

interface ModelSchema {
  success: boolean;
  result?: {
    input?: JsonSchema;
    output?: JsonSchema;
  };
}

interface ApiResponse {
  success: boolean;
  result?: ApiModel[];
}

interface CustomModel {
  name: string;
  description: string;
  task: {
    id: string;
    name: string;
    description: string;
  };
  schema: {
    input?: JsonSchema;
    output?: JsonSchema;
  };
}

interface CustomModelsConfig {
  models: CustomModel[];
}

interface ModelRouterProvider {
  name: string;
  type: string;
  models: string[];
  modelMapping?: Record<string, string>;
}

interface ModelRouterConfig {
  providers: ModelRouterProvider[];
  routing: Record<string, string[]>;
}

