function createSchemaSignature(schema: JsonSchema): string {
  // Create a normalized string representation for comparison that includes property names
  function normalizeSchema(obj: any): any {
    if (obj === null || typeof obj !== 'object') {
      return obj;
    }

    if (Array.isArray(obj)) {
      return obj.map(normalizeSchema);
    }

    const normalized: any = {};
    // Sort keys to ensure consistent ordering
    const sortedKeys = Object.keys(obj).sort();
    for (const key of sortedKeys) {
      normalized[key] = normalizeSchema(obj[key]);
    }
    return normalized;
  }

  return JSON.stringify(normalizeSchema(schema));
}

async function loadModelRouterConfig(): Promise<{ models: ApiModel[], schemas: Record<string, ModelSchema> }> {
  try {
    if (!fs.existsSync(MODEL_ROUTER_CONFIG)) {
      console.log(`No model router config found at ${MODEL_ROUTER_CONFIG}`);
      return { models: [], schemas: {} };
    }

    const content = fs.readFileSync(MODEL_ROUTER_CONFIG, 'utf-8');
    const config = JSON.parse(content) as ModelRouterConfig;

    console.log(`Loading external models from model router config with ${config.providers.length} providers`);

    const models: ApiModel[] = [];
    const schemas: Record<string, ModelSchema> = {};

    // Extract all unique models from providers
    const uniqueModels = new Set<string>();
    config.providers.forEach(provider => {
      provider.models.forEach(model => uniqueModels.add(model));
    });

    // Create ApiModel entries for external models
    uniqueModels.forEach(modelName => {
      // Skip Baseten test models - we only want the main external models
      if (modelName === 'gpt-oss-120b-test') {
        console.log(`⚠ Skipping Baseten test model: ${modelName}`);
        return;
      }
      // Determine capability based on model name patterns - using same logic as generateModelCatalog
      let capability = 'chat';
      let taskName = 'Text Generation';
      
      if (modelName.includes('whisper')) {
        capability = 'audio';
        taskName = 'Automatic Speech Recognition';
      } else if (modelName === 'bge-embeddings') {
        // Rename HuggingFace bge-embeddings to just 'embeddings' to match docs
        capability = 'embeddings';
        taskName = 'Text Embeddings';
      } else if (modelName.includes('embedding') || modelName.includes('bge')) {
        capability = 'embeddings';
        taskName = 'Text Embeddings';
      } else if (modelName === 'piiranha-pii') {
        capability = 'pii-detection';
        taskName = 'PII Detection';
      } else if (modelName.includes('melotts') || modelName.includes('aura')) {
        capability = 'tts';
        taskName = 'Text to Speech';
      } else if (modelName.includes('llava') || modelName.includes('vision') || modelName === 'llama-4-maverick-17b') {
        capability = 'chat'; // Primary capability - generateModelCatalog will add 'vision' for multimodal
        taskName = 'Text Generation';
      }

      models.push({
        id: modelName,
        name: modelName,
        description: `External model routed through model-router: ${modelName}`,
        task: {
          id: capability,
          name: taskName,
          description: `${taskName} via external model router`
        },
        created_at: new Date().toISOString(),
        tags: ['external', 'model-router'],
        properties: []
      });

      // Create schema for external models
      if (capability === 'audio') {
        // Whisper-like models
        schemas[modelName] = {
          success: true,
          result: {
            input: {
              type: 'object',
              properties: {
                file: {
                  type: 'string',
                  format: 'binary',
                  description: 'Audio file to transcribe'
                },
                model: {
                  type: 'string',
                  description: 'Model to use for transcription',
                  default: modelName
                },
                language: {
                  type: 'string',
                  description: 'Language of the audio'
                },
                prompt: {
                  type: 'string',
                  description: 'Optional prompt to guide transcription'
                },
                response_format: {
                  type: 'string',
                  enum: ['json', 'text', 'srt', 'verbose_json', 'vtt'],
                  default: 'json'
                },
                temperature: {
                  type: 'number',
                  minimum: 0,
                  maximum: 1,
                  default: 0
                }
              },
              required: ['file']
            },
            output: {
              type: 'object',
              properties: {
                text: {
                  type: 'string',
                  description: 'Transcribed text'
                }
              },
              required: ['text']
            }
          }
        };
      } else if (capability === 'embeddings') {
        // Embedding models
        schemas[modelName] = {
          success: true,
          result: {
            input: {
              type: 'object',
              properties: {
                input: {
                  oneOf: [
                    { type: 'string' },
                    { type: 'array', items: { type: 'string' } }
                  ],
                  description: 'Text to embed'
                },
                model: {
                  type: 'string',
                  description: 'Model to use for embeddings',
                  default: modelName
                },
                encoding_format: {
                  type: 'string',
                  enum: ['float', 'base64'],
                  default: 'float'
                },
                dimensions: {
                  type: 'integer',
                  description: 'Number of dimensions in the embedding'
                }
              },
              required: ['input']
            },
            output: {
              type: 'object',
              properties: {
                data: {
                  type: 'array',
                  items: {
                    type: 'object',
                    properties: {
                      embedding: {
                        type: 'array',
                        items: { type: 'number' }
                      },
                      index: {
                        type: 'integer'
                      }
                    },
                    required: ['embedding', 'index']
                  }
                },
                model: {
                  type: 'string'
                },
                usage: {
                  type: 'object',
                  properties: {
                    prompt_tokens: { type: 'integer' },
                    total_tokens: { type: 'integer' }
                  }
                }
              },
              required: ['data']
            }
          }
        };
      } else {
        // Chat/completion models with full OpenAI-compatible interface
        schemas[modelName] = {
          success: true,
          result: {
            input: {
              type: 'object',
              properties: {
                messages: {
                  type: 'array',
                  items: {
                    type: 'object',
                    properties: {
                      role: {
                        type: 'string',
                        enum: ['system', 'user', 'assistant', 'tool']
                      },
                      content: {
                        oneOf: [
                          { type: 'string' },
                          { type: 'null' }
                        ]
                      },
                      tool_calls: {
                        type: 'array',
                        items: {
                          type: 'object',
                          properties: {
                            id: { type: 'string' },
                            type: { type: 'string', enum: ['function'] },
                            function: {
                              type: 'object',
                              properties: {
                                name: { type: 'string' },
                                arguments: { type: 'string' }
                              },
                              required: ['name', 'arguments']
                            }
                          },
                          required: ['id', 'type', 'function']
                        }
                      },
                      tool_call_id: { type: 'string' },
                      name: { type: 'string' },
                      reasoning: { type: 'string' }
                    },
                    required: ['role']
                  }
                },
                model: {
                  type: 'string',
                  default: modelName
                },
                temperature: {
                  type: 'number',
                  minimum: 0,
                  maximum: 2,
                  default: 0.7
                },
                max_tokens: {
                  type: 'integer',
                  minimum: 1,
                  default: 4096
                },
                top_p: {
                  type: 'number',
                  minimum: 0,
                  maximum: 1
                },
                frequency_penalty: {
                  type: 'number',
                  minimum: -2,
                  maximum: 2
                },
                presence_penalty: {
                  type: 'number',
                  minimum: -2,
                  maximum: 2
                },
                stream: {
                  type: 'boolean',
                  default: false
                },
                response_format: {
                  type: 'object',
                  properties: {
                    type: {
                      type: 'string',
                      enum: ['json_object', 'json_schema', 'text'],
                      default: 'text'
                    },
                    json_schema: {
                      type: 'object',
                      properties: {
                        name: { type: 'string' },
                        description: { type: 'string' },
                        schema: { type: 'object' },
                        strict: { type: 'boolean' }
                      }
                    }
                  }
                },
                tools: {
                  type: 'array',
                  items: {
                    type: 'object',
                    properties: {
                      type: { type: 'string', enum: ['function'] },
                      function: {
                        type: 'object',
                        properties: {
                          name: { type: 'string' },
                          description: { type: 'string' },
                          parameters: { type: 'object' }
                        },
                        required: ['name']
                      }
                    },
                    required: ['type', 'function']
                  }
                },
                tool_choice: {
                  oneOf: [
                    { type: 'string', enum: ['none', 'auto', 'required'] },
                    {
                      type: 'object',
                      properties: {
                        type: { type: 'string', enum: ['function'] },
                        function: {
                          type: 'object',
                          properties: {
                            name: { type: 'string' }
                          },
                          required: ['name']
                        }
                      },
                      required: ['type', 'function']
                    }
                  ]
                },
                reasoning_effort: {
                  type: 'string',
                  enum: ['low', 'medium', 'high']
                }
              },
              required: ['messages']
            },
            output: {
              type: 'object',
              properties: {
                choices: {
                  type: 'array',
                  items: {
                    type: 'object',
                    properties: {
                      message: {
                        type: 'object',
                        properties: {
                          role: {
                            type: 'string',
                            enum: ['assistant']
                          },
                          content: {
                            oneOf: [
                              { type: 'string' },
                              { type: 'null' }
                            ]
                          },
                          tool_calls: {
                            type: 'array',
                            items: {
                              type: 'object',
                              properties: {
                                id: { type: 'string' },
                                type: { type: 'string', enum: ['function'] },
                                function: {
                                  type: 'object',
                                  properties: {
                                    name: { type: 'string' },
                                    arguments: { type: 'string' }
                                  },
                                  required: ['name', 'arguments']
                                }
                              },
                              required: ['id', 'type', 'function']
                            }
                          },
                          reasoning: {
                            type: 'string'
                          }
                        },
                        required: ['role']
                      },
                      finish_reason: {
                        type: 'string',
                        enum: ['stop', 'length', 'content_filter', 'tool_calls']
                      }
                    },
                    required: ['message']
                  }
                },
                usage: {
                  type: 'object',
                  properties: {
                    prompt_tokens: { type: 'integer' },
                    completion_tokens: { type: 'integer' },
                    total_tokens: { type: 'integer' }
                  }
                }
              },
              required: ['choices']
            }
          }
        };
      }
    });

    console.log(`Loaded ${models.length} external models from model router config`);
    return { models, schemas };
  } catch (error) {
    console.warn('Failed to load model router config:', error);
    return { models: [], schemas: {} };
  }
}

async function loadCustomModels(): Promise<{ models: ApiModel[], schemas: Record<string, ModelSchema> }> {
  try {
    if (!fs.existsSync(CUSTOM_MODELS_FILE)) {
      console.log(`No custom models file found at ${CUSTOM_MODELS_FILE}`);
      return { models: [], schemas: {} };
    }

    const content = fs.readFileSync(CUSTOM_MODELS_FILE, 'utf-8');
    const config = JSON.parse(content) as CustomModelsConfig;

    console.log(`Loading ${config.models.length} custom models from ${CUSTOM_MODELS_FILE}`);

    const models: ApiModel[] = config.models.map(m => ({
      id: m.name,
      name: m.name,
      description: m.description,
      task: m.task,
      created_at: new Date().toISOString(),
      tags: ['custom'],
      properties: []
    }));

    const schemas: Record<string, ModelSchema> = {};
    config.models.forEach(m => {
      if (m.schema) {
        schemas[m.name] = {
          success: true,
          result: {
            input: m.schema.input,
            output: m.schema.output
          }
        };
      }
    });

    console.log(`Loaded ${models.length} custom models with ${Object.keys(schemas).length} schemas`);
    return { models, schemas };
  } catch (error) {
    console.warn('Failed to load custom models:', error);
    return { models: [], schemas: {} };
  }
}

